function initMap(){var e=new google.maps.Map(document.getElementById("map"),{zoom:2,center:{lat:26.2639368,lng:12.7765974},scrollwheel:!1}),o=locationCoords.map(function(e){return new google.maps.Marker({position:e})});new MarkerClusterer(e,o,{imagePath:"https://developers.google.com/maps/documentation/javascript/examples/markerclusterer/m"})}var google,locationCoords=[],locationName=[];$.ajax({url:"locations.json",dataType:"text",success:function(e){for(var o=$.parseJSON(e),t=0;t<o.length;t++){var a={lat:o[t].latlng[0],lng:o[t].latlng[1]},n=o[t].name;locationCoords.push(a),locationName.push(n)}for(var l=0;l<locationName.length;l++){var r="<li>"+locationName[l]+"</li>";document.getElementById("visitedList").innerHTML+=r}initMap(),document.getElementById("congrats").innerHTML="<p>Congrats! You have visited "+locationName.length+" countries.</p><p>"+Math.round(locationName.length/248*100)+"% of the 248 countries in the world.</p>"}});